using System.Collections; using System.Collections.Generic; using UnityEngine;  public class controll : MonoBehaviour {     public GameObject cursor;     // Start is called before the first frame update     void Start()     {              }      // Update is called once per frame     void Update()     {         if (Input.GetKey(KeyCode.DownArrow))         {             Vector3 position = cursor.transform.position;             position += (0.3f * Vector3.forward);             cursor.transform.position = position;         }         if (Input.GetKey(KeyCode.UpArrow))         {             Vector3 position = cursor.transform.position;             position -= (0.3f * Vector3.forward);             cursor.transform.position = position;         }         if (Input.GetKey(KeyCode.LeftArrow))         {             Vector3 position = cursor.transform.position;             position += (0.3f * Vector3.left);             cursor.transform.position = position;         }         if (Input.GetKey(KeyCode.RightArrow))         {             Vector3 position = cursor.transform.position;             position += (0.3f * Vector3.right);             cursor.transform.position = position;         }     } }  
