using System.Collections; using System.Collections.Generic; using UnityEngine;  public class edge : MonoBehaviour {     public GameObject Vertex0, Vertex1;     public float edgelength = 1.5f;     // Start is called before the first frame update     void Start()     {              }      // Update is called once per frame     void Update()     {         for (int repeat = 0; repeat < 10; repeat++)         {             Vector3 vec0 = Vertex0.transform.position;             Vector3 vec1 = Vertex1.transform.position;             Vector3 vec01 = vec1 - vec0;             float dist = vec01.magnitude;             float diff = (dist - edgelength) * 0.1f;             Vector3 diffVec = diff * vec01.normalized;             vec0 += diffVec;             vec1 -= diffVec;             Vertex0.transform.position = vec0;             Vertex1.transform.position = vec1;         }      } }